import React, { useState, useEffect, useRef } from 'react';
import { getAIMessage } from '../api/api'; 
import './ChatWindow.css';

const ChatWindow = ({ partData, onProductSelect, selectedChat, onMessagesUpdate, language }) => {
  const [messages, setMessages] = useState(selectedChat ? selectedChat.messages : []);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    if (selectedChat) {
      setMessages(selectedChat.messages);
    } else {
      setMessages([]);
    }
  }, [selectedChat]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  useEffect(() => {
    if (messages.length > 0) {
      onMessagesUpdate(messages);
    }
  }, [messages, onMessagesUpdate]);

  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = 'auto';
      textarea.style.height = `${textarea.scrollHeight}px`;
    }
  }, [input]);

  const parseMessageText = (text) => {
    // Split the text by line breaks
    const lines = text.split('\n');

    return lines.map((line, lineIndex) => {
      // Match Markdown links: [text](url)
      const linkRegex = /\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g;
      const parts = [];
      let lastIndex = 0;
      let match;

      // Process links
      while ((match = linkRegex.exec(line)) !== null) {
        const [fullMatch, linkText, url] = match;
        const startIndex = match.index;

        // Add text before the link
        if (startIndex > lastIndex) {
          const beforeText = line.slice(lastIndex, startIndex);
          parts.push(parseBoldText(beforeText, `${lineIndex}-${lastIndex}`));
        }

        // Add the link
        parts.push(
          <a
            key={`${lineIndex}-${startIndex}`}
            href={url}
            target="_blank"
            rel="noopener noreferrer"
            style={{ color: '#2A3F44', textDecoration: 'underline' }}
          >
            {linkText}
          </a>
        );

        lastIndex = startIndex + fullMatch.length;
      }

      // Add remaining text after the last link
      if (lastIndex < line.length) {
        const remainingText = line.slice(lastIndex);
        parts.push(parseBoldText(remainingText, `${lineIndex}-${lastIndex}`));
      }

      // If no links were found, parse the entire line for bold text
      if (parts.length === 0) {
        parts.push(parseBoldText(line, `${lineIndex}-0`));
      }

      // Add a <br /> after each line except the last one
      return (
        <React.Fragment key={lineIndex}>
          {parts}
          {lineIndex < lines.length - 1 && <br />}
        </React.Fragment>
      );
    });
  };

  // Helper function to parse bold text (**text**)
  const parseBoldText = (text, keyPrefix) => {
    const parts = text.split(/(\*\*[^*]+\*\*)/g);
    return parts.map((part, partIndex) => {
      if (part.startsWith('**') && part.endsWith('**')) {
        const boldText = part.slice(2, -2);
        return <strong key={`${keyPrefix}-${partIndex}`}>{boldText}</strong>;
      }
      return part;
    });
  };

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user', role: 'user', content: input };
    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput('');
    setIsTyping(true);

    console.log('Raw user input:', input);

    const partNumberRegex = /PS-?\s?\d+/gi;
    const partNumbers = input.match(partNumberRegex) || [];
    console.log('Extracted part numbers:', partNumbers);

    console.log('partData:', partData);
    console.log('partData keys:', Object.keys(partData));

    let matchingProducts = [];
    let validPartNumbers = [];
    if (partNumbers.length > 0) {
      const normalizedPartNumbers = partNumbers.map(pn => pn.replace(/[-\s]/g, '').trim().toUpperCase());
      validPartNumbers = normalizedPartNumbers.filter(pn => partData[pn]);
      matchingProducts = validPartNumbers.map(pn => partData[pn]);
      console.log('Found parts in local partData:', matchingProducts);
    }

    if (matchingProducts.length > 0) {
      const botMessage = {
        text: language === 'English' ? 'Here are the parts I found:' : 'Aquí están las piezas que encontré:',
        sender: 'bot',
        role: 'assistant',
        content: language === 'English' ? 'Here are the parts I found:' : 'Aquí están las piezas que encontré:',
        partNumbers: validPartNumbers,
      };
      console.log('Bot message to be added (local data):', botMessage);
      setMessages(prev => {
        const newMessages = [...prev, botMessage];
        console.log('Messages after update (local data):', newMessages);
        setIsTyping(false);
        return newMessages;
      });

      console.log('Selected products (local data):', matchingProducts);
      onProductSelect(matchingProducts);
    } else {
      try {
        const apiMessages = updatedMessages.map(msg => ({
          role: msg.role,
          content: msg.content,
        }));

        const apiResponse = await getAIMessage(apiMessages, language);
        console.log('API response:', apiResponse);

        const apiPartNumbers = apiResponse.content.match(partNumberRegex) || [];
        const normalizedApiPartNumbers = apiPartNumbers.map(pn => pn.replace(/[-\s]/g, '').trim().toUpperCase());
        const validApiPartNumbers = normalizedApiPartNumbers.filter(pn => partData[pn]);
        const apiProducts = validApiPartNumbers.map(pn => partData[pn]);

        const botMessage = {
          text: apiResponse.content,
          sender: 'bot',
          role: 'assistant',
          content: apiResponse.content,
          partNumbers: validApiPartNumbers,
        };
        setMessages(prev => {
          const newMessages = [...prev, botMessage];
          console.log('Messages after update (API data):', newMessages);
          setIsTyping(false);
          return newMessages;
        });

        if (apiProducts.length > 0) {
          console.log('Selected products (API data):', apiProducts);
          onProductSelect(apiProducts);
        }
      } catch (error) {
        console.error('Error fetching response from API:', error);
        const botMessage = {
          text: language === 'English'
            ? 'An error occurred while fetching the response. Please try again or contact support at ' + (partData["Support Phone Number"] || '1-888-738-4871') + '.'
            : 'Ocurrió un error al obtener la respuesta. Por favor, intenta de nuevo o contacta al soporte en ' + (partData["Support Phone Number"] || '1-888-738-4871') + '.',
          sender: 'bot',
          role: 'assistant',
          content: language === 'English'
            ? 'An error occurred while fetching the response. Please try again or contact support at ' + (partData["Support Phone Number"] || '1-888-738-4871') + '.'
            : 'Ocurrió un error al obtener la respuesta. Por favor, intenta de nuevo o contacta al soporte en ' + (partData["Support Phone Number"] || '1-888-738-4871') + '.',
        };
        setMessages(prev => {
          const newMessages = [...prev, botMessage];
          console.log('Messages after update (API error):', newMessages);
          setIsTyping(false);
          return newMessages;
        });
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="chat-window">
      <div className="chat-messages">
        {messages.length === 0 && !isTyping ? (
          <div className="welcome-message">
            <h2>
              {language === 'English'
                ? '✨ Assistance for your appliance needs'
                : '✨ Asistencia para tus necesidades de electrodomésticos'}
            </h2>
          </div>
        ) : (
          <>
            {messages.map((message, index) => (
              <div key={index} className={`message ${message.sender}`}>
                <div className="message-content">
                  {parseMessageText(message.text)}
                </div>
              </div>
            ))}
            {isTyping && (
              <div className="message bot">
                <div className="message-content typing">
                  {language === 'English' ? 'Typing...' : 'Escribiendo...'}
                </div>
              </div>
            )}
          </>
        )}
        <div ref={messagesEndRef} />
      </div>
      <div className="chat-input">
        <textarea
          ref={textareaRef}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyPress}
          placeholder={
            language === 'English'
              ? 'My next question goes here'
              : 'Mi próxima pregunta va aquí'
          }
          className="chat-textarea"
          rows="1"
        />
        <button onClick={handleSendMessage}>➤</button>
      </div>
    </div>
  );
};

export default ChatWindow;
