import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api";
import { marked } from "marked";

// Mock data simulating parts from Refrigerator-Parts.htm and Dishwasher-Parts.htm
const mockPartData = {
  "Refrigerator Door Shelf Bin": {
    partNumber: "PS11752778",
    name: "Refrigerator Door Shelf Bin WPW10321304",
    price: "$36.18",
    image: "https://partselectcom-gtcdcddbene3cpes.z01.azurefd.net/11752778-1-L-Whirlpool-WPW10321304-Refrigerator-Door-Shelf-Bin.jpg",
  },
  "Dishwasher Rack": {
    partNumber: "PS11752309",
    name: "Dishwasher Rack WPW10296855",
    price: "$45.99",
    image: "https://partselectcom-gtcdcddbene3cpes.z01.azurefd.net/11752309-1-L-Whirlpool-WPW10296855-Shallow-Door-Bin.jpg",
  },
  "PS11752778": {
    partNumber: "PS11752778",
    name: "Refrigerator Door Shelf Bin WPW10321304",
    price: "$36.18",
    image: "https://partselectcom-gtcdcddbene3cpes.z01.azurefd.net/11752778-1-L-Whirlpool-WPW10321304-Refrigerator-Door-Shelf-Bin.jpg",
  },
  "PS11752309": {
    partNumber: "PS11752309",
    name: "Dishwasher Rack WPW10296855",
    price: "$45.99",
    image: "https://partselectcom-gtcdcddbene3cpes.z01.azurefd.net/11752309-1-L-Whirlpool-WPW10296855-Shallow-Door-Bin.jpg",
  },
};

function ChatWindow() {
  const VolefaultMessage = [{
    role: "assistant",
    content: "Hi, how can I help you with Refrigerator or Dishwasher parts today?"
  }];

  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false); // Added typing indicator
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, isTyping]);

  const handleSend = async () => {
    if (input.trim() === "") return;

    const newUserMessage = { role: "user", content: input };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput("");
    setIsTyping(true);

    try {
      const aiResponse = await getAIMessage(updatedMessages);
      setMessages([...updatedMessages, aiResponse]);
    } catch (error) {
      console.error("Error:", error);
      setMessages([
        ...updatedMessages,
        { role: "assistant", content: "An error occurred. Please try again." },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const renderMessage = (message, index) => {
    // Detect part number or product name
    const partNumberRegex = /Part Number: (\w+)/;
    const partNumberMatch = message.content.match(partNumberRegex);
    const productNames = Object.keys(mockPartData).filter(key => !key.startsWith("PS")); // Filter out part numbers
    const productNameMatch = productNames.find(name => message.content.includes(name));

    if (message.role === "assistant" && (partNumberMatch || productNameMatch)) {
      const key = partNumberMatch ? partNumberMatch[1] : productNameMatch;
      const product = mockPartData[key];
      if (product) {
        return (
          <div key={index} className={`${message.role}-message-container`}>
            <div className={`message ${message.role}-message`}>
              <div dangerouslySetInnerHTML={{ __html: marked(message.content).replace(/<p>|<\/p>/g, "") }} />
            </div>
            <div className="product-card">
              <img src={product.image} alt={product.name} />
              <div>
                <h3>{product.name}</h3>
                <p>{product.price}</p>
                <button onClick={() => addToCart(product)}>Add to Cart</button>
              </div>
            </div>
          </div>
        );
      }
    }

    return (
      <div key={index} className={`${message.role}-message-container`}>
        <div className={`message ${message.role}-message`}>
          <div dangerouslySetInnerHTML={{ __html: marked(message.content).replace(/<p>|<\/p>/g, "") }} />
        </div>
      </div>
    );
  };

  const addToCart = (product) => {
    console.log(`Added ${product.name} to cart`);
    alert(`${product.name} added to cart!`);
  };

  return (
    <div className="messages-container">
      {messages.map((message, index) => renderMessage(message, index))}
      {isTyping && (
        <div className="message assistant-message typing">
          <div>Typing...</div>
        </div>
      )}
      <div ref={messagesEndRef} />
      <div className="input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask about Refrigerator or Dishwasher parts..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              handleSend();
              e.preventDefault();
            }
          }}
          rows="3"
        />
        <button className="send-button" onClick={handleSend}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
