import React, { useState, useEffect, useRef } from 'react';
import { getAIMessage } from '../api/api'; 
import './ChatWindow.css';

const ChatWindow = ({ partData, onProductSelect, selectedChat, onMessagesUpdate }) => {
  const [messages, setMessages] = useState(selectedChat ? selectedChat.messages : []);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (selectedChat) {
      setMessages(selectedChat.messages);
    } else {
      setMessages([]);
    }
  }, [selectedChat]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isTyping]);

  useEffect(() => {
    if (messages.length > 0) {
      onMessagesUpdate(messages);
    }
  }, [messages, onMessagesUpdate]);

  // Helper function to parse text and bold **text**
  const parseMessageText = (text) => {
    const parts = text.split(/(\*\*[^*]+\*\*)/g); // Split by **text** pattern
    return parts.map((part, index) => {
      if (part.startsWith('**') && part.endsWith('**')) {
        const boldText = part.slice(2, -2); // Remove the ** markers
        return <strong key={index}>{boldText}</strong>;
      }
      return part;
    });
  };

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user', role: 'user', content: input };
    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput('');
    setIsTyping(true);

    console.log('Raw user input:', input);

    const partNumberRegex = /PS-?\s?\d+/gi;
    const partNumbers = input.match(partNumberRegex) || [];
    console.log('Extracted part numbers:', partNumbers);

    console.log('partData:', partData);
    console.log('partData keys:', Object.keys(partData));

    let matchingProducts = [];
    let validPartNumbers = [];
    if (partNumbers.length > 0) {
      const normalizedPartNumbers = partNumbers.map(pn => pn.replace(/[-\s]/g, '').trim().toUpperCase());
      validPartNumbers = normalizedPartNumbers.filter(pn => partData[pn]);
      matchingProducts = validPartNumbers.map(pn => partData[pn]);
      console.log('Found parts in local partData:', matchingProducts);
    }

    if (matchingProducts.length > 0) {
      const botMessage = {
        text: 'Here are the parts I found:',
        sender: 'bot',
        role: 'assistant',
        content: 'Here are the parts I found:',
        partNumbers: validPartNumbers,
      };
      console.log('Bot message to be added (local data):', botMessage);
      setMessages(prev => {
        const newMessages = [...prev, botMessage];
        console.log('Messages after update (local data):', newMessages);
        setIsTyping(false);
        return newMessages;
      });

      console.log('Selected products (local data):', matchingProducts);
      onProductSelect(matchingProducts);
    } else {
      try {
        const apiMessages = updatedMessages.map(msg => ({
          role: msg.role,
          content: msg.content,
        }));

        const apiResponse = await getAIMessage(apiMessages);
        console.log('API response:', apiResponse);

        const apiPartNumbers = apiResponse.content.match(partNumberRegex) || [];
        const normalizedApiPartNumbers = apiPartNumbers.map(pn => pn.replace(/[-\s]/g, '').trim().toUpperCase());
        const validApiPartNumbers = normalizedApiPartNumbers.filter(pn => partData[pn]);
        const apiProducts = validApiPartNumbers.map(pn => partData[pn]);

        const botMessage = {
          text: apiResponse.content,
          sender: 'bot',
          role: 'assistant',
          content: apiResponse.content,
          partNumbers: validApiPartNumbers,
        };
        setMessages(prev => {
          const newMessages = [...prev, botMessage];
          console.log('Messages after update (API data):', newMessages);
          setIsTyping(false);
          return newMessages;
        });

        if (apiProducts.length > 0) {
          console.log('Selected products (API data):', apiProducts);
          onProductSelect(apiProducts);
        }
      } catch (error) {
        console.error('Error fetching response from API:', error);
        const botMessage = {
          text: 'An error occurred while fetching the response. Please try again or contact support at ' + (partData["Support Phone Number"] || '1-888-738-4871') + '.',
          sender: 'bot',
          role: 'assistant',
          content: 'An error occurred while fetching the response. Please try again or contact support at ' + (partData["Support Phone Number"] || '1-888-738-4871') + '.',
        };
        setMessages(prev => {
          const newMessages = [...prev, botMessage];
          console.log('Messages after update (API error):', newMessages);
          setIsTyping(false);
          return newMessages;
        });
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="chat-window">
      <div className="chat-messages">
        {messages.length === 0 && !isTyping ? (
          <div className="welcome-message">
            <h2>✨ Assisstance for your appliance needs</h2>
          </div>
        ) : (
          <>
            {messages.map((message, index) => (
              <div key={index} className={`message ${message.sender}`}>
                <div className="message-content">
                  {parseMessageText(message.text)}
                  {/* Removed the part-numbers section to avoid links */}
                </div>
              </div>
            ))}
            {isTyping && (
              <div className="message bot">
                <div className="message-content typing">
                  Typing...
                </div>
              </div>
            )}
          </>
        )}
        <div ref={messagesEndRef} />
      </div>
      <div className="chat-input">
        <button className="order-support-btn">✨</button>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Ask about your appliance parts"
        />
        <button onClick={handleSendMessage}>➤</button>
      </div>
    </div>
  );
};

export default ChatWindow;
