import React, { useState, useEffect } from 'react';
import ChatStorage from './components/ChatStorage';
import ChatWindow from './components/ChatWindow';
import ProductCard from './components/ProductCard';
import logo from './logo.png';
import partData from './partData.json'; 
import './App.css';

const App = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [language, setLanguage] = useState('English');
  const [partDataState, setPartData] = useState({});
  const [selectedProducts, setSelectedProducts] = useState([]);

  useEffect(() => {
    console.log('Loaded partData:', partData);
    setPartData(partData);
  }, []);

  const handleNewChat = () => {
    // Save the current chat if it has messages
    if (selectedChat && selectedChat.messages && selectedChat.messages.length > 0) {
      const firstMessage = selectedChat.messages[0]?.text || '';
      const chatName = firstMessage.length > 0 
        ? firstMessage.substring(0, 20) + (firstMessage.length > 20 ? '...' : '')
        : `Chat ${new Date().toLocaleDateString()}`;
      const newChatEntry = {
        title: chatName,
        messages: selectedChat.messages,
        createdAt: new Date().toISOString(), // Add timestamp for grouping
      };
      setChats(prevChats => [...prevChats, newChatEntry]);
    }

    // Start a new chat
    const newChat = {
      title: language === 'English' ? 'New Chat' : 'Nueva Conversación',
      messages: [],
      createdAt: new Date().toISOString(),
    };
    setSelectedChat(newChat);
    setSelectedProducts([]); // Reset product sidebar
  };

  const handleSelectChat = (chat) => {
    setSelectedChat(chat);
    setSelectedProducts([]);
  };

  const handleDeleteChat = (index) => {
    setChats(prevChats => prevChats.filter((_, i) => i !== index));
    // If the deleted chat is the currently selected chat, start a new chat
    if (selectedChat && chats[index] === selectedChat) {
      handleNewChat();
    }
  };

  const handleProductSelect = (products) => {
    setSelectedProducts(products);
  };

  const toggleLanguage = (lang) => {
    setLanguage(lang);
  };

  const handleMessagesUpdate = (updatedMessages) => {
    if (selectedChat) {
      setSelectedChat({ ...selectedChat, messages: updatedMessages });
    }
  };

  return (
    <div className="app">
      <div className="top-bar">
          <a href="https://www.partselect.com" target="_blank" rel="noopener noreferrer" className="logo">
            <img src={logo} alt="PartSelect Logo" className="logo-image" />
          </a>
          <div className="language-toggle">
            <button
              className={language === 'English' ? 'active' : ''}
              onClick={() => toggleLanguage('English')}
            >
              English
            </button>
            <button
              className={language === 'Spanish' ? 'active' : ''}
              onClick={() => toggleLanguage('Spanish')}
            >
              Spanish
            </button>
        </div>
      </div>
      <div className="main-content">
        <ChatStorage
          chats={chats}
          onSelectChat={handleSelectChat}
          onNewChat={handleNewChat}
          onDeleteChat={handleDeleteChat} // Pass delete handler
          language={language}
          selectedChat={selectedChat} // Pass selectedChat for active highlighting
        />
        <ChatWindow
          partData={partDataState}
          onProductSelect={handleProductSelect}
          selectedChat={selectedChat}
          onMessagesUpdate={handleMessagesUpdate}
          language={language}
        />
        <div className="product-sidebar">
          {selectedProducts.length > 0 ? (
            selectedProducts.map((product, index) => (
              <ProductCard key={index} product={product} language={language} />
            ))
          ) : (
            <div className="placeholder">
              {language === 'English' ? (
                <>
                  Generated product items with purchase information will appear here!
                </>
              ) : (
                <>
                  Los artículos de producto generados con información de compra aparecerán aquí!
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
