import React, { useState, useEffect } from 'react';
import './App.css';
import ChatWindow from './components/ChatWindow';

function App() {
  const [partData, setPartData] = useState(null); // State to hold the fetched partData

  useEffect(() => {
    // Fetch the latest partData.json from the server
    const fetchPartData = async () => {
      try {
        const response = await fetch('http://localhost:5000/scrape-and-get-data');
        const data = await response.json();
        setPartData(data);
      } catch (error) {
        console.error('Error fetching part data:', error);
        // Fallback to static partData.json if fetch fails
        try {
          const staticData = await import('./partData.json');
          setPartData(staticData.default);
        } catch (staticError) {
          console.error('Error loading static partData.json:', staticError);
          setPartData({ "Support Phone Number": "1-888-738-4871" }); // Minimal fallback
        }
      }
    };

    fetchPartData();
  }, []); // Empty dependency array to run only on mount

  if (!partData) {
    return <div>Loading...</div>; // Show loading state while fetching
  }

  // Read the phone number from partData, with a fallback
  const phoneNumber = partData["Support Phone Number"] || '1-888-738-4871';

  return (
    <div className="App">
      <div className="top-bar">
        <div className="logo">PartSelect</div>
      </div>
      <div className="nav-bar">
        <div className="nav-item">
          <i className="fas fa-magnifying-glass nav-icon"></i> Find Product
        </div>
        <div className="nav-item">
          <i className="fas fa-headset nav-icon"></i> Order Support: <a href={`tel:${phoneNumber}`}>{phoneNumber}</a>
        </div>
      </div>
      <div className="main-content">
        <div className="chat-window">
          <ChatWindow partData={partData} />
        </div>
      </div>
    </div>
  );
}

export default App;
