import React, { useState, useEffect, useRef } from "react";
import { getAIMessage } from "../api";

function ChatWindow() {
  const [messages, setMessages] = useState([
    {
      role: "assistant",
      content:
        "Hello! I'm here to help with Refrigerator and Dishwasher parts. How can I assist you today?",
    },
  ]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const chatContainerRef = useRef(null);

  // Scroll to the bottom when messages update
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages, isTyping]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newUserMessage = { role: "user", content: input };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput("");
    setIsTyping(true);

    try {
      const aiResponse = await getAIMessage(updatedMessages);
      setMessages([...updatedMessages, aiResponse]);
    } catch (error) {
      console.error("Error sending message:", error);
      setMessages([
        ...updatedMessages,
        { role: "assistant", content: "An error occurred. Please try again." },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  const renderMessage = (message, index) => {
    const partNumberRegex = /Part Number: (\w+)/;
    const match = message.content.match(partNumberRegex);

    if (message.role === "assistant" && match) {
      const partNumber = match[1];
      // Mock product data (replace with API call in production)
      const product = {
        number: partNumber,
        name: `Part ${partNumber}`,
        price: "$19.99",
        image: "/part.jpg",
      };
      return (
        <div className="chat-bubble bot" key={index}>
          <p>{message.content}</p>
          <div className="product-card">
            <img src={product.image} alt={product.name} />
            <div>
              <h3>{product.name}</h3>
              <p>{product.price}</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          </div>
        </div>
      );
    }
    return (
      <div className={`chat-bubble ${message.role}`} key={index}>
        <p>{message.content}</p>
      </div>
    );
  };

  const addToCart = (product) => {
    // Mock cart functionality (replace with actual cart integration)
    console.log(`Added ${product.name} to cart`);
    alert(`${product.name} added to cart!`);
  };

  return (
    <div className="chat-window">
      <div className="chat-messages" ref={chatContainerRef}>
        {messages.map((message, index) => renderMessage(message, index))}
        {isTyping && (
          <div className="chat-bubble typing">
            <p>Typing...</p>
          </div>
        )}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Ask about parts, installation, or orders..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default ChatWindow;
