export const getAIMessage = async (conversation) => {
  const systemMessage = {
    role: "system",
    content: `You are a helpful assistant for the PartSelect website. You answer ONLY questions related to Refrigerator and Dishwasher parts. You can help users:
    - Find compatible parts (e.g., "Is this part compatible with my model?")
    - Answer installation questions (e.g., "How do I install a Refrigerator Door Shelf Bin?")
    - Troubleshoot common issues (e.g., "My ice maker isn't working")
    - Help with order support (e.g., "What's the status of my order?")

    When responding, include the product name (e.g., "Refrigerator Door Shelf Bin") or part number (e.g., "Part Number: PS11752778") if relevant. 
    Do not generate part numbers.
    If the user asks anything outside this scope (e.g., oven parts), politely decline with: "I'm sorry, I can only assist with Refrigerator and Dishwasher parts."`
  };

  const messages = [systemMessage, ...conversation];

  try {
    const apiKey = process.env.REACT_APP_DEEPSEEK_API_KEY;
    const response = await fetch("https://api.deepseek.com/v1/chat/completions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${apiKey}`,
      },
      body: JSON.stringify({
        model: "deepseek-chat",
        messages: messages,
        temperature: 0.7,
        max_tokens: 300,
      }),
    });

    const data = await response.json();
    const message = {
      role: "assistant",
      content: data.choices[0].message.content,
    };

    return message;
  } catch (error) {
    console.error("DeepSeek API error:", error);
    return {
      role: "assistant",
      content: "Sorry, I couldn't connect to the assistant right now.",
    };
  }
};
