import React, { useState, useEffect } from 'react';
import ChatStorage from './components/ChatStorage';
import ChatWindow from './components/ChatWindow';
import ProductCard from './components/ProductCard';
import './App.css';

const App = () => {
  const [chats, setChats] = useState([
    { title: 'Lorem ipsum DOLEM', category: 'this-week', messages: [] },
    { title: 'Lorem ipsum DOLEM', category: 'this-week', messages: [] },
    { title: 'Lorem ipsum DOLEM', category: 'this-week', messages: [] },
    { title: 'Lorem ipsum DOLEM', category: '2024', messages: [] },
    { title: 'Lorem ipsum DOLEM', category: '2024', messages: [] },
  ]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [language, setLanguage] = useState('English');
  const [partData, setPartData] = useState({});
  const [selectedProduct, setSelectedProduct] = useState(null);

  useEffect(() => {
    // Load partData.json
    fetch('/partData.json')
      .then(response => response.json())
      .then(data => setPartData(data))
      .catch(error => console.error('Error loading part data:', error));
  }, []);

  const handleNewChat = () => {
    const newChat = {
      title: 'Lorem ipsum DOLEM',
      category: 'this-week',
      messages: [],
    };
    setChats([...chats, newChat]);
    setSelectedChat(newChat);
    setSelectedProduct(null);
  };

  const handleSelectChat = (chat) => {
    setSelectedChat(chat);
    setSelectedProduct(null);
  };

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
  };

  const toggleLanguage = (lang) => {
    setLanguage(lang);
    // Functionality for language toggle to be implemented later
  };

  return (
    <div className="app">
      <div className="top-bar">
        <div className="logo">
          <span role="img" aria-label="house">üè†</span> PartSelect
          <span className="tagline">Here to help since 1999</span>
        </div>
        <div className="language-toggle">
          <button
            className={language === 'English' ? 'active' : ''}
            onClick={() => toggleLanguage('English')}
          >
            English
          </button>
          <button
            className={language === 'Spanish' ? 'active' : ''}
            onClick={() => toggleLanguage('Spanish')}
          >
            Spanish
          </button>
        </div>
      </div>
      <div className="main-content">
        <ChatStorage
          chats={chats}
          onSelectChat={handleSelectChat}
          onNewChat={handleNewChat}
        />
        <ChatWindow
          partData={partData}
          onProductSelect={handleProductSelect}
          selectedChat={selectedChat}
        />
        <div className="product-sidebar">
          {selectedProduct ? (
            <ProductCard product={selectedProduct} />
          ) : (
            <div className="placeholder">
              Generated product items with purchase information will appear here!
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
