// src/components/ProductCard.js
import React, { useState } from 'react';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  // If no product is selected, show the message bubble
  const [quantity, setQuantity] = useState(1);
  if (!product) {
    return (
      <div className="message-bubble">
        Please select a product to view details
      </div>
    );
  }

  // Extract the star rating percentage (e.g., "95%" -> 95)
  const ratingPercent = parseFloat(product.starRating) || 0;

  const handleIncrement = () => {
    setQuantity(prev => prev + 1);
  };

  // Handle quantity decrement (minimum 1)
  const handleDecrement = () => {
    setQuantity(prev => (prev > 1 ? prev - 1 : 1));
  };

  return (
    <div className="product-card-container">
      <h2>{product.name}</h2>

      <img src={product.image} alt={product.name} className="product-image" />

      <div className="quantity-bar"> 
        <span>Quantity</span>
        <div className="quantity-controls">
          <button className="quantity-btn" onClick={handleDecrement}>-</button>
          <span className="quantity-value">1</span>
          <button className="quantity-btn" onClick={handleIncrement}>+</button>
        </div>
      </div>

      <div className="rating">
        <div className="star-rating">
          <div className="stars-empty">★★★★★</div>
          <div className="stars-filled" style={{ width: `${ratingPercent}%` }}>
            ★★★★★
          </div>
        </div>
        <span className="reviews">({product.numberOfReviews} Reviews)</span>
      </div>

      <h3 className="description-title">Product Description</h3>
      <p className="description">{product.description}</p>

      <span className="price">{product.price}</span>

      <a
        href={product.partUrl}
        target="_blank"
        rel="noopener noreferrer"
        className="view-in-store-btn"
      >
        View in Store
      </a>
    </div>
  );
};

export default ProductCard;
