import React, { useEffect, useRef } from 'react';
import './ChatStorage.css';

const ChatStorage = ({ chats, onSelectChat, onNewChat }) => {
  const chatStorageRef = useRef(null);

  // Scroll to the top when the component mounts or chats change
  useEffect(() => {
    if (chatStorageRef.current) {
      chatStorageRef.current.scrollTop = 0;
    }
  }, [chats]);

  return (
    <div className="chat-storage" ref={chatStorageRef}>
      <button className="new-chat-btn" onClick={onNewChat}>
        + New Chat
      </button>
      {chats.length === 0 ? (
        <div className="no-chats">No previous chats</div>
      ) : (
        chats.map((chat, index) => (
          <div
            key={index}
            className="chat-item"
            onClick={() => onSelectChat(chat)}
          >
            {chat.title}
          </div>
        ))
      )}
    </div>
  );
};

export default ChatStorage;
