import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api";
import { marked } from "marked";
import partData from "../partData.json";

function ChatWindow() {
  const defaultMessage = [{
    role: "assistant",
    content: "Hi, how can I help you with your appliance parts today?"
  }];

  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, isTyping]);

  const handleSend = async () => {
    if (input.trim() === "") return;

    const newUserMessage = { role: "user", content: input };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput("");
    setIsTyping(true);

    try {
      const aiResponse = await getAIMessage(updatedMessages);
      setMessages([...updatedMessages, aiResponse]);
    } catch (error) {
      console.error("Error:", error);
      setMessages([
        ...updatedMessages,
        { role: "assistant", content: "An error occurred. Please try again." },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const renderMessage = (message, index) => {
    // Extract all part numbers mentioned in the message
    const partNumberRegex = /Part Number: (\w+)/g;
    const mentionedPartNumbers = [];
    let match;
    while ((match = partNumberRegex.exec(message.content)) !== null) {
      mentionedPartNumbers.push(match[1].toUpperCase());
    }

    // Identify invalid part numbers (those not in partData)
    const invalidPartNumbers = mentionedPartNumbers.filter(pn => !partData.hasOwnProperty(pn));

    // Check for a single part number or product name to display a product card
    const partNumberMatch = message.content.match(/Part Number: (\w+)/);
    const productNames = Object.keys(partData).filter(key => !key.startsWith("PS"));
    const productNameMatch = productNames.find(name => message.content.includes(name));
    const key = partNumberMatch ? partNumberMatch[1].toUpperCase() : productNameMatch;
    const product = key ? partData[key] : null;

    // Add a note if there are invalid part numbers
    let additionalNote = '';
    if (invalidPartNumbers.length > 0) {
      additionalNote = `<p style="color: red;">Note: The following part numbers are not available in our database: ${invalidPartNumbers.join(', ')}</p>`;
    }

    // Render message with product card if a valid product is found
    if (message.role === "assistant" && product) {
      return (
        <div key={index} className={`${message.role}-message-container`}>
          <div className={`message ${message.role}-message`}>
            <div dangerouslySetInnerHTML={{ __html: marked(message.content).replace(/<p>|<\/p>/g, "") + additionalNote }} />
          </div>
          <div className="product-card">
            <img src={product.image} alt={product.name} />
            <div>
              <h3>{product.name}</h3>
              <p>{product.price}</p>
            </div>
          </div>
        </div>
      );
    }

    // Render message without product card if no valid product is found
    return (
      <div key={index} className={`${message.role}-message-container`}>
        <div className={`message ${message.role}-message`}>
          <div dangerouslySetInnerHTML={{ __html: marked(message.content).replace(/<p>|<\/p>/g, "") + additionalNote }} />
        </div>
      </div>
    );
  };

  return (
    <div className="messages-container">
      {messages.map((message, index) => renderMessage(message, index))}
      {isTyping && (
        <div className="message assistant-message typing">
          <div>Typing...</div>
        </div>
      )}
      <div ref={messagesEndRef} />
      <div className="input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask about any parts..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              handleSend();
              e.preventDefault();
            }
          }}
          rows="3"
        />
        <button className="send-button" onClick={handleSend}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
