import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api";
import { marked } from "marked";
import partData from "../partData.json";

function ChatWindow() {
  const defaultMessage = [{
    role: "assistant",
    content: "Hi, how can I help you with your appliance parts today?"
  }];

  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, isTyping]);

  const handleSend = async () => {
    if (input.trim() === "") return;

    const newUserMessage = { role: "user", content: input };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput("");
    setIsTyping(true);

    try {
      const aiResponse = await getAIMessage(updatedMessages);
      setMessages([...updatedMessages, aiResponse]);
    } catch (error) {
      console.error("Error:", error);
      setMessages([
        ...updatedMessages,
        { role: "assistant", content: "An error occurred. Please try again." },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const renderMessage = (message, index) => {
    // Preprocess the message to remove invalid part numbers
    let modifiedContent = message.content;
    const partNumberRegex = /Part Number: (\w+)/g;
    const mentionedPartNumbers = [];
    let match;

    // Extract all part numbers mentioned in the message
    while ((match = partNumberRegex.exec(message.content)) !== null) {
      mentionedPartNumbers.push(match[1].toUpperCase());
    }

    // Replace invalid part numbers with a placeholder
    mentionedPartNumbers.forEach(pn => {
      if (!partData.hasOwnProperty(pn)) {
        const partNumberText = `Part Number: ${pn}`;
        modifiedContent = modifiedContent.replace(
          partNumberText,
          "[a replacement part may be needed, but the specific part number is unavailable]"
        );
      }
    });

    // Check for a single valid part number or product name to display a product card
    const partNumberMatch = modifiedContent.match(/Part Number: (\w+)/);
    const productNames = Object.keys(partData).filter(key => !key.startsWith("PS"));
    const productNameMatch = productNames.find(name => modifiedContent.includes(name));
    const key = partNumberMatch ? partNumberMatch[1].toUpperCase() : productNameMatch;
    const product = key ? partData[key] : null;

    // Render message with product card if a valid product is found
    if (message.role === "assistant" && product) {
      return (
        <div key={index} className={`${message.role}-message-container`}>
          <div className={`message ${message.role}-message`}>
            <div dangerouslySetInnerHTML={{ __html: marked(modifiedContent).replace(/<p>|<\/p>/g, "") }} />
          </div>
          <div className="product-card">
            <img src={product.image} alt={product.name} />
            <div className="product-details">
              <h3>{product.name}</h3>
              <p><strong>Part Number:</strong> {product.partNumber || key}</p>
              <p><strong>Price:</strong> {product.price}</p>
              {product.compatibility && <p><strong>Compatibility:</strong> {product.compatibility}</p>}
              {product.description && <p><strong>Description:</strong> {product.description}</p>}
            </div>
          </div>
        </div>
      );
    }

    // Render message without product card if no valid product is found
    return (
      <div key={index} className={`${message.role}-message-container`}>
        <div className={`message ${message.role}-message`}>
          <div dangerouslySetInnerHTML={{ __html: marked(modifiedContent).replace(/<p>|<\/p>/g, "") }} />
        </div>
      </div>
    );
  };

  return (
    <div className="messages-container">
      {messages.map((message, index) => renderMessage(message, index))}
      {isTyping && (
        <div className="message assistant-message typing">
          <div>Typing...</div>
        </div>
      )}
      <div ref={messagesEndRef} />
      <div className="input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask about any parts..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              handleSend();
              e.preventDefault();
            }
          }}
          rows="3"
        />
        <button className="send-button" onClick={handleSend}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
