import React, { useState, useEffect, useRef } from 'react';
import './ChatWindow.css';

const ChatWindow = ({ partData, onProductSelect, selectedChat, onMessagesUpdate }) => {
  const [messages, setMessages] = useState(selectedChat ? selectedChat.messages : []);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (selectedChat) {
      setMessages(selectedChat.messages);
    } else {
      setMessages([]);
    }
  }, [selectedChat]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  useEffect(() => {
    // Update the parent component whenever messages change
    if (messages.length > 0) {
      onMessagesUpdate(messages);
    }
  }, [messages, onMessagesUpdate]);

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages([...messages, userMessage]);
    setInput('');

    // Extract part numbers from the message (e.g., PS12364199)
    const partNumberRegex = /PS\d+/g;
    const partNumbers = input.match(partNumberRegex) || [];
    const validPartNumbers = partNumbers.filter(pn => partData[pn]);

    if (validPartNumbers.length > 0) {
      const botMessage = {
        text: 'Here are the parts I found:',
        sender: 'bot',
        partNumbers: validPartNumbers,
      };
      setMessages(prev => [...prev, botMessage]);

      // Select the first product to display in the sidebar
      const firstProduct = partData[validPartNumbers[0]];
      onProductSelect(firstProduct);
    } else {
      const botMessage = {
        text: 'I couldn’t find any parts matching that number. Please try again or contact support at ' + (partData["Support Phone Number"] || '1-888-738-4871') + '.',
        sender: 'bot',
      };
      setMessages(prev => [...prev, botMessage]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="chat-window">
      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="welcome-message">
            <div className="sparkles">✨</div>
            <h2>Ask our Bot anything</h2>
          </div>
        ) : (
          messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              <div className="message-content">
                {message.text}
                {message.partNumbers && (
                  <div className="part-numbers">
                    {message.partNumbers.map((partNumber, idx) => {
                      const product = partData[partNumber];
                      return (
                        <div key={idx} className="product-link">
                          <button onClick={() => onProductSelect(product)}>
                            {product.name}
                          </button>
                        </div>
                      );
                    })}
                  </div>
                )}
              </div>
              {message.sender === 'bot' && (
                <div className="message-actions">
                  <button>Make response shorter</button>
                  <button>Explain it simpler</button>
                  <button>Tell me more about this part</button>
                </div>
              )}
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>
      <div className="chat-input">
        <button className="order-support-btn">✨ Order Support</button>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="My next question goes here"
        />
        <button onClick={handleSendMessage}>➤</button>
      </div>
    </div>
  );
};

export default ChatWindow;
