import React from 'react';
import './ChatStorage.css';

const ChatStorage = ({ chats, onSelectChat, onNewChat, language }) => {
  const groupedChats = chats.reduce((acc, chat) => {
    const category = chat.category || 'this-week';
    if (!acc[category]) {
      acc[category] = [];
    }
    acc[category].push(chat);
    return acc;
  }, {});

  return (
    <div className="chat-storage">
      <button className="new-chat-btn" onClick={onNewChat}>
        {language === 'English' ? '+ New Chat' : '+ Nueva Conversación'}
      </button>
      {Object.keys(groupedChats).map(category => (
        <div key={category} className="chat-group">
          <h3>
            {category === 'this-week'
              ? language === 'English'
                ? 'This Week'
                : 'Esta Semana'
              : category === 'last-week'
              ? language === 'English'
                ? 'Last Week'
                : 'La Semana Pasada'
              : category === 'last-month'
              ? language === 'English'
                ? 'Last Month'
                : 'El Mes Pasado'
              : category}
          </h3>
          {groupedChats[category].map((chat, index) => (
            <div
              key={index}
              className="chat-item"
              onClick={() => onSelectChat(chat)}
            >
              {chat.title}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default ChatStorage;
