import React, { useState, useEffect } from 'react';
import ChatStorage from './ChatStorage';
import ChatWindow from './ChatWindow';
import ProductCard from './ProductCard';
import logo from './logo.png';
import partData from './partData.json'; // Import directly from src
import './App.css';

const App = () => {
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [language, setLanguage] = useState('English');
  const [partDataState, setPartData] = useState({});

  useEffect(() => {
    console.log('Loaded partData:', partData); // Debug log
    setPartData(partData);
  }, []);

  const handleNewChat = () => {
    if (selectedChat && selectedChat.messages && selectedChat.messages.length > 0) {
      const chatName = selectedChat.messages[0]?.text?.substring(0, 20) || `Chat ${new Date().toLocaleDateString()}`;
      const newChatEntry = {
        title: chatName,
        messages: selectedChat.messages,
        category: 'this-week',
      };
      setChats(prevChats => [...prevChats, newChatEntry]);
    }

    const newChat = {
      title: 'New Chat',
      messages: [],
      category: 'this-week',
    };
    setSelectedChat(newChat);
    setSelectedProduct(null);
  };

  const handleSelectChat = (chat) => {
    setSelectedChat(chat);
    setSelectedProduct(null);
  };

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
  };

  const toggleLanguage = (lang) => {
    setLanguage(lang);
  };

  const handleMessagesUpdate = (updatedMessages) => {
    if (selectedChat) {
      setSelectedChat({ ...selectedChat, messages: updatedMessages });
    }
  };

  return (
    <div className="app">
      <div className="top-bar">
        <div className="logo">
          <img src={logo} alt="PartSelect Logo" className="logo-image" />
          <span className="tagline">Here to help since 1999</span>
        </div>
        <div className="language-toggle">
          <button
            className={language === 'English' ? 'active' : ''}
            onClick={() => toggleLanguage('English')}
          >
            English
          </button>
          <button
            className={language === 'Spanish' ? 'active' : ''}
            onClick={() => toggleLanguage('Spanish')}
          >
            Spanish
          </button>
        </div>
      </div>
      <div className="main-content">
        <ChatStorage
          chats={chats}
          onSelectChat={handleSelectChat}
          onNewChat={handleNewChat}
        />
        <ChatWindow
          partData={partDataState}
          onProductSelect={handleProductSelect}
          selectedChat={selectedChat}
          onMessagesUpdate={handleMessagesUpdate}
        />
        <div className="product-sidebar">
          {selectedProduct ? (
            <ProductCard product={selectedProduct} />
          ) : (
            <div className="placeholder">
              Generated product items with purchase information will appear here!
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
