// src/components/ChatStorage.js
import React from 'react';
import './ChatStorage.css';

// Helper function to format the chat's creation date into categories
const getTimeCategory = (date) => {
  const now = new Date();
  const chatDate = new Date(date);
  const diffInDays = Math.floor((now - chatDate) / (1000 * 60 * 60 * 24));

  if (diffInDays === 0) return 'Today';
  if (diffInDays === 1) return 'Yesterday';
  if (diffInDays <= 7) return 'This Week';
  return 'Older';
};

const ChatStorage = ({ chats, onSelectChat, onNewChat, language, onDeleteChat, selectedChat }) => {
  // Group chats by time category
  const groupedChats = chats.reduce((acc, chat) => {
    const category = getTimeCategory(chat.createdAt);
    if (!acc[category]) {
      acc[category] = [];
    }
    acc[category].push(chat);
    return acc;
  }, {});

  // Define category order
  const categoryOrder = ['Today', 'Yesterday', 'This Week', 'Older'];

  return (
    <div className="chat-storage">
      <button className="new-chat-btn" onClick={onNewChat}>
        {language === 'English' ? '+ New Chat' : '+ Nueva Conversación'}
      </button>
      {categoryOrder.map(category => (
        groupedChats[category] && groupedChats[category].length > 0 && (
          <div key={category} className="chat-group">
            <h3>
              {category === 'Today'
                ? language === 'English'
                  ? 'Today'
                  : 'Hoy'
                : category === 'Yesterday'
                ? language === 'English'
                  ? 'Yesterday'
                  : 'Ayer'
                : category === 'This Week'
                ? language === 'English'
                  ? 'This Week'
                  : 'Esta Semana'
                : language === 'English'
                ? 'Older'
                : 'Más Antiguo'}
            </h3>
            {groupedChats[category].map((chat, index) => (
              <div key={index} className="chat-item-container">
                <div
                  className={`chat-item ${selectedChat === chat ? 'active' : ''}`}
                  onClick={() => onSelectChat(chat)}
                >
                  {chat.title}
                </div>
                <button
                  className="delete-chat-btn"
                  onClick={() => onDeleteChat(index)}
                  title={language === 'English' ? 'Delete Chat' : 'Eliminar Conversación'}
                >
                  🗑️
                </button>
              </div>
            ))}
          </div>
        )
      ))}
    </div>
  );
};

export default ChatStorage;
