import React, { useState } from 'react';
import partData from '../partData.json'; // Adjust path as needed

function ChatWindow() {
  const [messages, setMessages] = useState([{ sender: 'Grok', text: 'Hi! Ask me about a part by name or part number.' }]);
  const [input, setInput] = useState('');

  // Function to find part by name or part number
  const findPart = (query) => {
    const lowerQuery = query.toLowerCase();
    for (const partNumber in partData) {
      const part = partData[partNumber];
      if (
        part.name.toLowerCase().includes(lowerQuery) ||
        part.partNumber.toLowerCase() === lowerQuery
      ) {
        return part;
      }
    }
    return null;
  };

  // Handle user input submission
  const handleSend = (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Add user message
    setMessages([...messages, { sender: 'You', text: input }]);

    // Check for part match
    const part = findPart(input.trim());
    const response = part
      ? {
          sender: 'Grok',
          text: `Found part!\nName: ${part.name}\nPart Number: ${part.partNumber}\nPrice: ${part.price}\nImage: ${part.image}`,
        }
      : { sender: 'Grok', text: `Sorry, I couldn’t find a part matching "${input}". Try another name or part number!` };

    // Add Grok's response
    setMessages((prev) => [...prev, response]);
    setInput(''); // Clear input
  };

  return (
    <div className="chat-window">
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender === 'Grok' ? 'grok-msg' : 'user-msg'}>
            <strong>{msg.sender}: </strong>
            <pre>{msg.text}</pre> {/* Use pre for formatted text */}
          </div>
        ))}
      </div>
      <form onSubmit={handleSend}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a part name or number..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default ChatWindow;
