import React, { useState, useEffect, useRef } from 'react';
import './ChatWindow.css';

const ChatWindow = ({ partData, onProductSelect, selectedChat, onMessagesUpdate }) => {
  const [messages, setMessages] = useState(selectedChat ? selectedChat.messages : []);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (selectedChat) {
      setMessages(selectedChat.messages);
    } else {
      setMessages([]);
    }
  }, [selectedChat]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  useEffect(() => {
    // Update the parent component whenever messages change
    if (messages.length > 0) {
      onMessagesUpdate(messages);
    }
  }, [messages, onMessagesUpdate]);

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages([...messages, userMessage]);
    setInput('');

    // Debug: Log the raw input
    console.log('Raw user input:', input);

    // Extract part numbers from the message (e.g., PS12364199, PS-12364199, PS 12364199), case-insensitive
    const partNumberRegex = /PS-?\s?\d+/gi;
    const partNumbers = input.match(partNumberRegex) || [];
    console.log('Extracted part numbers:', partNumbers);

    // Debug: Log the partData object and its keys
    console.log('partData:', partData);
    console.log('partData keys:', Object.keys(partData));

    // Check if any extracted part numbers exist in partData
    let localPart = null;
    let validPartNumber = null;
    if (partNumbers.length > 0) {
      const normalizedPartNumbers = partNumbers.map(pn => pn.replace(/[-\s]/g, '').trim().toUpperCase());
      validPartNumber = normalizedPartNumbers.find(pn => partData[pn]);
      if (validPartNumber) {
        localPart = partData[validPartNumber];
        console.log('Found part in local partData:', localPart);
      } else {
        console.log('No matching part found in local partData');
      }
    }

    // If a part is found in local partData, use it; otherwise, proceed with the API call
    if (localPart) {
      const botMessage = {
        text: 'Here are the parts I found:',
        sender: 'bot',
        partNumbers: [validPartNumber],
      };
      console.log('Bot message to be added (local data):', botMessage);
      setMessages(prev => {
        const newMessages = [...prev, botMessage];
        console.log('Messages after update (local data):', newMessages);
        return newMessages;
      });

      // Update the product card with the local part data
      console.log('Selected product (local data):', localPart);
      onProductSelect(localPart);
    } else {
      // Make an API call with the raw user input
      try {
        // Replace this with your actual API endpoint
        const response = await fetch('https://api.example.com/assist', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            // Add any required headers (e.g., API key)
            // 'Authorization': 'Bearer your-api-key',
          },
          body: JSON.stringify({ query: input }),
        });

        if (!response.ok) {
          throw new Error('Failed to fetch response from API');
        }

        const apiData = await response.json();
        console.log('API response:', apiData);

        // Assuming the API returns a response with a text field and optionally a part field
        const botMessage = {
          text: apiData.text || 'I received a response from the API, but it didn’t contain a message.',
          sender: 'bot',
          partNumbers: apiData.part ? [apiData.part.partNumber] : [],
        };
        setMessages(prev => {
          const newMessages = [...prev, botMessage];
          console.log('Messages after update (API data):', newMessages);
          return newMessages;
        });

        // If the API response includes a part, update the product card
        if (apiData.part) {
          console.log('Selected product (API data):', apiData.part);
          onProductSelect(apiData.part);
        }
      } catch (error) {
        console.error('Error fetching response from API:', error);
        const botMessage = {
          text: 'An error occurred while fetching the response. Please try again or contact support at ' + (partData["Support Phone Number"] || '1-888-738-4871') + '.',
          sender: 'bot',
        };
        setMessages(prev => {
          const newMessages = [...prev, botMessage];
          console.log('Messages after update (API error):', newMessages);
          return newMessages;
        });
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="chat-window">
      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="welcome-message">
            <div className="sparkles">✨</div>
            <h2>Ask our Bot anything</h2>
          </div>
        ) : (
          messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              <div className="message-content">
                {message.text}
                {message.partNumbers && message.partNumbers.length > 0 && (
                  <div className="part-numbers">
                    {message.partNumbers.map((partNumber, idx) => {
                      const product = partData[partNumber];
                      return (
                        <div key={idx} className="product-link">
                          <button onClick={() => onProductSelect(product)}>
                            {product ? product.name : partNumber}
                          </button>
                        </div>
                      );
                    })}
                  </div>
                )}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>
      <div className="chat-input">
        <button className="order-support-btn">✨ Order Support</button>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="My next question goes here"
        />
        <button onClick={handleSendMessage}>➤</button>
      </div>
    </div>
  );
};

export default ChatWindow;
