import React from 'react';
import './ChatStorage.css';

const ChatStorage = ({ chats, onSelectChat, onNewChat }) => {
  return (
    <div className="chat-storage">
      <button className="new-chat-btn" onClick={onNewChat}>
        + New Chat
      </button>
      <div className="chat-section">
        <h3>THIS WEEK</h3>
        {chats
          .filter(chat => chat.category === 'this-week')
          .map((chat, index) => (
            <div
              key={index}
              className="chat-item"
              onClick={() => onSelectChat(chat)}
            >
              {chat.title}
            </div>
          ))}
      </div>
      <div className="chat-section">
        <h3>2024</h3>
        {chats
          .filter(chat => chat.category === '2024')
          .map((chat, index) => (
            <div
              key={index}
              className="chat-item"
              onClick={() => onSelectChat(chat)}
            >
              {chat.title}
            </div>
          ))}
      </div>
    </div>
  );
};

export default ChatStorage;
