import React, { useState, useEffect, useRef } from "react";
import "./ChatWindow.css";
import { getAIMessage } from "../api/api";
import { marked } from "marked";
import partData from "../partData.json";

function ChatWindow() {
  const defaultMessage = [{
    role: "assistant",
    content: "Hi, how can I help you with your appliance parts today?"
  }];

  const [messages, setMessages] = useState(defaultMessage);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, isTyping]);

  const handleSend = async () => {
    if (input.trim() === "") return;

    const newUserMessage = { role: "user", content: input };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput("");
    setIsTyping(true);

    try {
      const aiResponse = await getAIMessage(updatedMessages);
      setMessages([...updatedMessages, aiResponse]);
    } catch (error) {
      console.error("Error:", error);
      setMessages([
        ...updatedMessages,
        { role: "assistant", content: "An error occurred. Please try again." },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const renderMessage = (message, index) => {
    // Preprocess the message to remove invalid part numbers
    let modifiedContent = message.content;
    const partNumberRegex = /\bPS\d+\b/g; // Match any "PS" followed by digits (e.g., PS12345)
    const mentionedPartNumbers = new Set(); // Use a Set to avoid duplicates
    let match;

    // Extract all part numbers mentioned in the message
    while ((match = partNumberRegex.exec(message.content)) !== null) {
      mentionedPartNumbers.add(match[0].toUpperCase());
    }

    // Replace invalid part numbers with a placeholder
    mentionedPartNumbers.forEach(pn => {
      if (!partData.hasOwnProperty(pn)) {
        const regex = new RegExp(`\\b${pn}\\b`, 'g'); // Match the exact part number
        modifiedContent = modifiedContent.replace(
          regex,
          "[a replacement part may be needed, but the specific part number is unavailable]"
        );
      }
    });

    // Find all valid part numbers to display product cards
    const validPartNumbers = Array.from(mentionedPartNumbers).filter(pn => partData.hasOwnProperty(pn));

    // Render message with product cards if valid products are found
    if (message.role === "assistant" && validPartNumbers.length > 0) {
      return (
        <div key={index} className={`${message.role}-message-container`}>
          <div className={`message ${message.role}-message`}>
            <div dangerouslySetInnerHTML={{ __html: marked(modifiedContent).replace(/<p>|<\/p>/g, "") }} />
          </div>
          <div className="product-cards-container">
            {validPartNumbers.map((partNumber, idx) => {
              const product = partData[partNumber];
              return (
                <div key={idx} className="product-card">
                  <img src={product.image} alt={product.name} />
                  <div className="product-details">
                    <h3>{product.name}</h3>
                    <p><strong>Part Number:</strong> {product.partNumber}</p>
                    <p><strong>Manufacturer Part Number:</strong> {product.manufacturerPartNumber || 'N/A'}</p>
                    <p><strong>Price:</strong> {product.price}</p>
                    {product.compatibility && <p><strong>Compatibility:</strong> {product.compatibility}</p>}
                    {product.description && <p><strong>Description:</strong> {product.description}</p>}
                    {product.partType && <p><strong>Part Type:</strong> {product.partType}</p>}
                    {product.appliance && <p><strong>Appliance:</strong> {product.appliance}</p>}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      );
    }

    // Render message without product cards if no valid products are found
    return (
      <div key={index} className={`${message.role}-message-container`}>
        <div className={`message ${message.role}-message`}>
          <div dangerouslySetInnerHTML={{ __html: marked(modifiedContent).replace(/<p>|<\/p>/g, "") }} />
        </div>
      </div>
    );
  };

  return (
    <div className="messages-container">
      {messages.map((message, index) => renderMessage(message, index))}
      {isTyping && (
        <div className="message assistant-message typing">
          <div>Typing...</div>
        </div>
      )}
      <div ref={messagesEndRef} />
      <div className="input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask about any parts..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              handleSend();
              e.preventDefault();
            }
          }}
          rows="3"
        />
        <button className="send-button" onClick={handleSend}>
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
